mapper.select:
SELECT FROM poetry_category WHERE is_deleted = 0
AND category_name LIKE CONCAT('%', #{categoryName}, '%')
AND category_code LIKE CONCAT('%', #{categoryCode}, '%')
AND parent_id = #{parentId}
AND status = #{status.value}
ORDER BY sort_order ASC, created_time DESC

mapper.update:
UPDATE poetry_category
SET is_deleted = 1
WHERE id = #{id}
